import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function TryVirtually() {
  const { productId } = useParams();  // Access productId from URL parameters
  const [clothImage, setClothImage] = useState(null);
  const [personImage, setPersonImage] = useState(null);
  const [renderedImage, setRenderedImage] = useState(null);

  const handleFileChange = (e, setImage) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('cloth_image', clothImage);
    formData.append('person_image', personImage);

    try {
      const response = await axios.post('http://localhost:5000/upload_images', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setRenderedImage(response.data.rendered_image);
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  return (
    <div>
      <h2>Virtual Try-On for Product {productId}</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={(e) => handleFileChange(e, setClothImage)} />
        <input type="file" onChange={(e) => handleFileChange(e, setPersonImage)} />
        <button type="submit">Try Virtually</button>
      </form>
      {renderedImage && <img src={`http://localhost:4000${renderedImage}`} alt="Rendered Virtual Try-On" />}
    </div>
  );
}

export default TryVirtually;
