import React, { useState } from 'react';
import '../TryVirtually/';

const TryVirtually = () => {
  const [uploadedImage, setUploadedImage] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setUploadedImage(URL.createObjectURL(file)); // Display the uploaded image
  };

  const openCamera = () => {
    // Implement camera opening logic here
    alert("Camera functionality will be implemented.");
  };

  return (
    <div className='tryvirtually'>
      <h1>Try Virtually</h1>
      <p>Upload an image or open the camera to try the product on.</p>

      <div className='tryvirtually-options'>
        {/* Upload Image Option */}
        <div className='tryvirtually-option'>
          <label htmlFor="upload">Upload Image</label>
          <input 
            type="file" 
            id="upload" 
            accept="image/*"
            onChange={handleFileUpload} 
          />
        </div>

        {/* Open Camera Option */}
        <div className='tryvirtually-option'>
          <button onClick={openCamera}>Open Camera</button>
        </div>
      </div>

      {/* Display the uploaded image */}
      {uploadedImage && (
        <div className='tryvirtually-preview'>
          <h3>Uploaded Image:</h3>
          <img src={uploadedImage} alt="Uploaded" className='tryvirtually-image'/>
        </div>
      )}
    </div>
  );
};

export default TryVirtually;
