import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const VirtualTry = () => {
  const { productId } = useParams();
  const [modelImage, setModelImage] = useState(null); // Uploaded or captured model image
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [renderedImage, setRenderedImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const webcamRef = useRef(null);
  const { addToCart, all_product } = useContext(ShopContext);

  const product = all_product.find(p => p.id === Number(productId));

  // Capture image from webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (imageSrc) {
      setModelImage(imageSrc);
      setIsCameraOpen(false);
    }
  }, [webcamRef]);

  // Upload both model and product images to backend
  const handleTryOn = async (event) => {
    const file = event.target.files[0];
    if (file && product) {
      setModelImage(URL.createObjectURL(file)); // Preview selected image
      setIsLoading(true);

      const formData = new FormData();
      formData.append('model', file); // Model image from file input
      formData.append('cloth', product.image); // Product image

      try {
        const response = await axios.post('http://localhost:4000/tryon', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });

        setRenderedImage(response.data.outputImagePath);
      } catch (error) {
        console.error('Error during try-on process:', error);
        alert('Try-on failed');
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  const closeModal = () => {
    setIsCameraOpen(false);
  };

  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id);
      alert('Product added to cart!');
    }
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        {product && (
          <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} className="preview-image" />
          </div>
        )}

        <div className="image-comparison-container">
          {modelImage && (
            <div className="image-preview">
              <h3 className="preview-title">Your Uploaded Image</h3>
              <img src={modelImage} alt="Preview" className="preview-image" />
            </div>
          )}
          {renderedImage && (
            <div className="image-rendered">
              <h3 className="rendered-title">Virtual Try-On Result</h3>
              <img src={`http://localhost:4000/images/${renderedImage}`} alt="Rendered" className="rendered-image" />
            </div>
          )}
        </div>

        {isLoading && <p>Processing your try-on... Please wait.</p>}

        <div className="button-section">
          <button onClick={handleOpenCamera} className="button open-camera-button">Open Camera</button>
          <label htmlFor="file-upload" className="button upload-image-button">Upload Image</label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleTryOn}
            className="file-input"
            style={{ display: 'none' }}
          />
        </div>

        {isCameraOpen && (
          <div className="camera-modal">
            <div className="camera-overlay" onClick={closeModal}></div>
            <div className="camera-content">
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                className="webcam"
              />
              <div className="camera-actions">
                <button onClick={captureImage} className="btn capture-button">Capture</button>
                <button onClick={closeModal} className="btn close-button">Close</button>
              </div>
            </div>
          </div>
        )}

        <button onClick={handleAddToCart} className="button add-to-cart-button">Add to Cart</button>
      </div>
    </div>
  );
};

export default VirtualTry;
