import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';

const VirtualTry = () => {
  const { productId } = useParams();
  const [image, setImage] = useState(null);
  const [renderedImage, setRenderedImage] = useState(null); // To display the result image from the server
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const webcamRef = useRef(null);
  const { addToCart, all_product } = useContext(ShopContext);

  const product = all_product.find(p => p.id === Number(productId));

  // Capture image from the camera
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    setIsCameraOpen(false);
    submitImages(imageSrc, product.image);
  }, [webcamRef]);

  // Handle image file upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      const imageUrl = reader.result;
      setImage(imageUrl);
      submitImages(imageUrl, product.image);
    };
    reader.readAsDataURL(file);
  };

  // Open the camera modal
  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  // Close the camera modal
  const closeModal = () => {
    setIsCameraOpen(false);
  };

  // Add product to cart
  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id);
      alert('Product added to cart!');
    }
  };

  // Submit images to server for virtual try-on processing
  const submitImages = async (userImage, productImage) => {
    try {
      const response = await fetch('http://localhost:4000/api/tryon', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userImage, productImage })
      });
      const data = await response.json();
      if (data.success) {
        setRenderedImage(`http://localhost:4000/${data.resultPath}`);
      } else {
        console.error('Error in virtual try-on processing:', data.message);
      }
    } catch (error) {
      console.error('Failed to submit images:', error);
    }
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        {product && (
          <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} className="preview-image" />
          </div>
        )}

        <div className="image-comparison-container">
          {image && (
            <div className="image-preview">
              <h3 className="preview-title">Your Uploaded Image</h3>
              <img src={image} alt="Preview" className="preview-image" />
            </div>
          )}
          {renderedImage && (
            <div className="image-rendered">
              <h3 className="rendered-title">Virtual Try-On Image</h3>
              <img src={renderedImage} alt="Rendered" className="rendered-image" />
            </div>
          )}
        </div>

        <div className="button-section">
          <button onClick={handleOpenCamera} className="button open-camera-button">Open Camera</button>
          <label htmlFor="file-upload" className="button upload-image-button">Upload Image</label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="file-input"
            style={{ display: 'none' }}
          />
        </div>

        {isCameraOpen && (
          <div className="camera-modal">
            <div className="camera-overlay" onClick={closeModal}></div>
            <div className="camera-content">
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                className="webcam"
              />
              <div className="camera-actions">
                <button onClick={captureImage} className="btn capture-button">Capture</button>
                <button onClick={closeModal} className="btn close-button">Close</button>
              </div>
            </div>
          </div>
        )}

        <button onClick={handleAddToCart} className="button add-to-cart-button">Add to Cart</button>
      </div>
    </div>
  );
};

export default VirtualTry;
