import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';

const VirtualTry = () => {
  const { productId } = useParams(); // Get productId from the URL
  const [image, setImage] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const webcamRef = useRef(null);
  const { addToCart, all_product } = useContext(ShopContext);

  // Find the product based on the productId
  const product = all_product.find(p => p.id === Number(productId));

  // Capture image from webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    setIsCameraOpen(false);
  }, [webcamRef]);

  const handleImageUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  const closeModal = () => {
    setIsCameraOpen(false);
  };

  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id); // Use the correct product ID
      alert('Product added to cart!'); // Notify user
    }
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        {product && (
          <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} />
          </div>
        )}

        <div className="upload-section">
          <label htmlFor="file-upload" className="custom-file-upload">
            Upload Image
          </label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="file-input"
          />
        </div>

        <div className="virtual-try-preview-container">
          {image && (
            <div>
              <h3 className="preview-title">Your Uploaded Image</h3>
              <img src={image} alt="Preview" className="preview-image" />
            </div>
          )}
          {/* Replace this with your 3D rendered image when available */}
          {image && (
            <div>
              <h3 className="rendered-title">3D Rendered Image</h3>
              <img src="path_to_your_rendered_image" alt="Rendered" className="rendered-image" />
            </div>
          )}
        </div>

        <div className="virtual-try-options">
          <button onClick={handleOpenCamera} className="btn try-button">
            Open Camera
          </button>

          {isCameraOpen && (
            <div className="camera-modal">
              <div className="camera-overlay" onClick={closeModal}></div>
              <div className="camera-content">
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="webcam"
                />
                <div className="camera-actions">
                  <button onClick={captureImage} className="btn capture-button">Capture</button>
                  <button onClick={closeModal} className="btn close-button">Close</button>
                </div>
              </div>
            </div>
          )}
        </div>

        <button onClick={handleAddToCart} className="btn add-to-cart-button">Add to Cart</button>
      </div>
    </div>
  );
};

export default VirtualTry;
