import './App.css';
import Navbar from './Components/Navbar/Navbar';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Shop from './Pages/Shop';
import ShopCategory from './Pages/ShopCategory';
import Product from './Pages/Product';
import Cart from './Pages/Cart';
import Profile from './Pages/Profile';
import LoginSignup from './Pages/LoginSignup';
import VirtualTry from './Components/TryVirtually/TryVirtually';  
import Footer from './Components/Footer/Footer';
import men_banner from './Components/Assets/banner_mens.png';
import women_banner from './Components/Assets/banner_women.png';
import kid_banner from './Components/Assets/banner_kids.png';
import OrderConfirmation from './Pages/OrderConfirmation';

import { ShopContext } from './'; // Assuming you store user/auth state here

function App() {
  const { isAuthenticated } = useContext(ShopContext); // Replace with your actual auth state/context

  return (
    <div>
      <BrowserRouter>
        <Navbar />
        <Routes>
          <Route path='/' element={<Shop />} />
          <Route path='/mens' element={<ShopCategory banner={men_banner} category="men" />} />
          <Route path='/womens' element={<ShopCategory banner={women_banner} category="women" />} />
          <Route path='/kids' element={<ShopCategory banner={kid_banner} category="kid" />} />
          <Route path='/product/:productId' element={<Product />} /> {/* Change here */}
          <Route path='/cart' element={<Cart />} />
          <Route
          path="/order-confirmation"
          element={
            isAuthenticated ? <OrderConfirmation /> : <Navigate to="/login" />
          }
        />
          <Route path='/login' element={<LoginSignup />} />
          <Route path='/virtualtry/:productId' element={<VirtualTry />} />  {/* Updated this route */}
          <Route path="/profile" element={<Profile />} />

        </Routes>
        <Footer />
      </BrowserRouter>
    </div>
  );
}

export default App;
