import React, { useState, useCallback, useRef } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';

const VirtualTry = () => {
  const [image, setImage] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [dressImage, setDressImage] = useState(null); // New state for the dress image
  const webcamRef = useRef(null);

  // Capture image from webcam (for display purposes)
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
  }, [webcamRef]);

  const handleImageUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
    setDressImage('/path/to/your/3d-dress-image.png'); // Replace with the path to the 3D dress image
  };

  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  const closeModal = () => {
    setIsCameraOpen(false);
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        <div className="virtual-try-options">
          <button onClick={handleOpenCamera} className="btn try-button">
            Open Camera
          </button>

          <input type="file" accept="image/*" onChange={handleImageUpload} className="file-input" />

          {/* Show comparison only when an image is uploaded */}
          {image && dressImage && (
            <div className="comparison-section">
              <div className="comparison-box">
                <h3 className="preview-title">Your Uploaded Image</h3>
                <img src={image} alt="Uploaded" className="comparison-image" />
              </div>
              <div className="comparison-box">
                <h3 className="preview-title">Virtual Try-On</h3>
                <img src={dressImage} alt="Dress" className="comparison-image" />
              </div>
            </div>
          )}

          {isCameraOpen && (
            <div className="camera-modal">
              <div className="camera-overlay" onClick={closeModal}></div>
              <div className="camera-content">
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="webcam"
                />
                <div className="camera-actions">
                  <button onClick={captureImage} className="btn capture-button">Show Dress on Video</button>
                  <button onClick={closeModal} className="btn close-button">Close</button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Render the dress overlay on the webcam */}
      {isCameraOpen && dressImage && (
        <div className="dress-overlay">
          {/* You can use CSS to position the dress image over the webcam video */}
          <img src={dressImage} alt="Dress Overlay" className="overlay-dress" />
        </div>
      )}
    </div>
  );
};

export default VirtualTry;
