import React, { useContext, useState } from 'react';
import './OrderConfirmation.css';
import { ShopContext } from '../Context/ShopContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const OrderConfirmation = () => {
  const { cartItems, all_product, getTotalCartAmount, clearCart } = useContext(ShopContext);
  const [address, setAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('Credit Card');
  const navigate = useNavigate();

  

  return (
    <div className='order-confirmation'>
      <h1>Order Confirmation</h1>
      <div className='order-items'>
        <h2>Cart Items</h2>
        {all_product.map((e) => {
          if (cartItems[e.id] > 0) {
            return (
              <div key={e.id} className='order-item'>
                <img src={e.image} alt={e.name} className='order-item-image' />
                <p>{e.name}</p>
                <p>Quantity: {cartItems[e.id]}</p>
                <p>Total: TK {e.new_price * cartItems[e.id]}</p>
              </div>
            );
          }
          return null;
        })}
      </div>
      <div className='order-summary'>
        <h3>Total Amount: TK {getTotalCartAmount()}</h3>
        <div className='order-address'>
          <label htmlFor='address'>Address:</label>
          <input
            type='text'
            id='address'
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder='Enter your delivery address'
          />
        </div>
        <div className='order-payment'>
          <label htmlFor='payment-method'>Payment Method:</label>
          <select
            id='payment-method'
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value='Credit Card'>Credit Card</option>
            <option value='Cash on Delivery'>Cash on Delivery</option>
            <option value='Mobile Banking'>Mobile Banking</option>
          </select>
        </div>
        <button onClick={handleConfirmOrder}>Confirm Order</button>
      </div>
    </div>
  );
};

export default OrderConfirmation;
