import React, { useContext, useState, useEffect } from 'react';
import './ConfirmOrder.css';
import { ShopContext } from '../../Context/ShopContext';
import { useNavigate } from 'react-router-dom';

const ConfirmOrder = () => {
  const { cartItems, all_product, getTotalCartAmount } = useContext(ShopContext);
  const [shippingInfo, setShippingInfo] = useState({
    fullName: '',
    district: '',
    address: '',
  });
  const [paymentMethod, setPaymentMethod] = useState({
    card: false,
    cash: false,
  });
  const navigate = useNavigate();

  // Handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle payment method selection
  const handlePaymentChange = (e) => {
    const { name, checked } = e.target;
    setPaymentMethod((prevState) => ({
      ...prevState,
      [name]: checked,
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can add functionality to submit the order
    console.log('Order Submitted', { shippingInfo, paymentMethod });
    // Redirect to confirmation or another page
    navigate('/orderconfirmation');
  };

  // Filter out products added to cart
  const cartProducts = all_product.filter((product) => cartItems[product.id] > 0);

  return (
    <div className="confirmorder">
      <div className="confirmorder-left">
        <h2>Shipping Address</h2>
        <form onSubmit={handleSubmit}>
          <div className="confirmorder-form">
            <label>Full Name</label>
            <input
              type="text"
              name="fullName"
              value={shippingInfo.fullName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="confirmorder-form">
            <label>District</label>
            <input
              type="text"
              name="district"
              value={shippingInfo.district}
              onChange={handleChange}
              required
            />
          </div>
          <div className="confirmorder-form">
            <label>Address</label>
            <textarea
              name="address"
              value={shippingInfo.address}
              onChange={handleChange}
              required
            ></textarea>
          </div>

          <h3>Payment Method</h3>
          <div className="confirmorder-payment">
            <div className="confirmorder-payment-option">
              <input
                type="checkbox"
                name="card"
                checked={paymentMethod.card}
                onChange={handlePaymentChange}
              />
              <label>Card</label>
            </div>
            <div className="confirmorder-payment-option">
              <input
                type="checkbox"
                name="cash"
                checked={paymentMethod.cash}
                onChange={handlePaymentChange}
              />
              <label>Cash</label>
            </div>
          </div>

          <button type="submit" className="confirmorder-submit">Submit Order</button>
        </form>
      </div>

      <div className="confirmorder-right">
        <h2>Order Summary</h2>
        {cartProducts.map((product) => (
          <div key={product.id} className="confirmorder-item">
            <img src={product.image} alt={product.name} className="confirmorder-item-image" />
            <div className="confirmorder-item-info">
              <p>{product.name}</p>
              <p>TK {product.new_price} x {cartItems[product.id]}</p>
              <p>Total: TK {product.new_price * cartItems[product.id]}</p>
            </div>
          </div>
        ))}
        <div className="confirmorder-total">
          <p>Total Amount: TK {getTotalCartAmount()}</p>
        </div>
      </div>
    </div>
  );
};

export default ConfirmOrder;
