import React, { useState, useContext } from "react";
import "./ConfirmOrder.css"; // Add your CSS for styling
import { ShopContext } from "../../Context/ShopContext";
import { useNavigate } from "react-router-dom";

const ConfirmOrder = () => {
    const { cartItems, all_product, getTotalCartAmount } = useContext(ShopContext);
    const navigate = useNavigate();

    const [fullName, setFullName] = useState("");
    const [city, setCity] = useState("");
    const [address, setAddress] = useState("");
    const [paymentMethod, setPaymentMethod] = useState("");
    const [message, setMessage] = useState(null); // State for success/error message
    const [isLoading, setIsLoading] = useState(false); // State for loading

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setMessage(null); // Reset message
        
        // Validate form fields
        if (!fullName || !city || !address || !paymentMethod) {
            setMessage({ type: "error", text: "Please fill in all fields." });
            setIsLoading(false);
            return;
        }
    
        const orderDetails = {
            fullName,
            city,
            address,
            paymentMethod,
            cartItems: cartItems, // pass cart items
            totalAmount: getTotalCartAmount(), // total amount of the order
        };
    
        console.log("Order Details:", orderDetails);
    
        try {
            const response = await fetch("http://localhost:4000/confirmorder", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "auth-token": localStorage.getItem("token"), // Send auth token
                },
                body: JSON.stringify(orderDetails), // Send order details as the request body
            });
    
            console.log("Response Status:", response.status);
            const data = await response.json();
            console.log("Response Data:", data);
    
            if (data.success) {
                setMessage({ type: "success", text: "Order confirmed successfully!" });
                setTimeout(() => navigate("/order-success"), 2000); // Navigate after 2 seconds
            } else {
                setMessage({ type: "error", text: data.message || "Failed to confirm order." });
            }
        } catch (error) {
            setMessage({ type: "error", text: "Failed to confirm order. Try again later." });
            console.error("Error:", error);
        } finally {
            setIsLoading(false);
        }
    };
    
    // Filter cart items to show only those that have quantity > 0
    const cartItemsList = all_product.filter(product => cartItems[product.id] > 0);

    return (
        <div className="confirm-order-container">
            <div className="confirm-order-form">
                <h2>Shipping Address</h2>
                {message && (
                    <div
                        style={{
                            color: message.type === "error" ? "red" : "green",
                            marginBottom: "10px",
                        }}
                    >
                        {message.text}
                    </div>
                )}
                <form onSubmit={handleSubmit}>
                    <label>
                        Full Name
                        <input
                            type="text"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            required
                        />
                    </label>

                    <label>
                        City
                        <input
                            type="text"
                            value={city}
                            onChange={(e) => setCity(e.target.value)}
                            required
                        />
                    </label>

                    <label>
                        Address
                        <textarea
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                            required
                        />
                    </label>

                    <h3>Select Payment Method</h3>
                    <div className="payment-methods">
                        <label>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value="Cash"
                                checked={paymentMethod === "Cash"}
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            Cash on Delivery
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value="Card"
                                checked={paymentMethod === "Card"}
                                onChange={(e) => setPaymentMethod(e.target.value)}
                            />
                            Credit/Debit Card
                        </label>
                    </div>

                    <button type="submit" disabled={isLoading}>
                        {isLoading ? "Processing..." : "Confirm Order"}
                    </button>
                </form>
            </div>

            <div className="confirm-order-summary">
                <h2>Order Summary</h2>
                <div className="order-items">
                    {cartItemsList.map((product) => (
                        <div key={product.id} className="order-item">
                            <img src={product.image} alt={product.name} />
                            <div className="order-item-info">
                                <p>{product.name}</p>
                                <p>Quantity: {cartItems[product.id]}</p>
                                <p>Total: TK {product.new_price * cartItems[product.id]}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="order-total">
                    <p>Subtotal: TK {getTotalCartAmount()}</p>
                    <p>Shipping: Free</p>
                    <h3>Total: TK {getTotalCartAmount()}</h3>
                </div>
            </div>
        </div>
    );
};

export default ConfirmOrder;
