import React, { useContext, useState } from 'react';
import './ConfirmOrder.css';  // You can add styling as needed
import { ShopContext } from '../../Context/ShopContext';
import { useNavigate } from 'react-router-dom';

const ConfirmOrder = () => {
  const { cartItems, all_product, getTotalCartAmount } = useContext(ShopContext);
  const [shippingDetails, setShippingDetails] = useState({
    fullName: '',
    district: '',
    address: '',
  });
  const [paymentMethod, setPaymentMethod] = useState('cash');  // Default to cash
  const navigate = useNavigate();

  // Handle input change for shipping details
  const handleShippingChange = (e) => {
    setShippingDetails({
      ...shippingDetails,
      [e.target.name]: e.target.value,
    });
  };

  // Handle payment method selection
  const handlePaymentChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  // Handle order submission
  const handleSubmitOrder = async () => {
    if (!shippingDetails.fullName || !shippingDetails.district || !shippingDetails.address) {
      alert('Please fill in all the shipping details.');
      return;
    }

    // Create the ConfirmOrder object
    const confirmOrder = {
      shippingAddress: shippingDetails,
      paymentMethod: paymentMethod,
      cartItems: cartItems,  // Include cart items in the order data
      totalAmount: getTotalCartAmount(),  // Get the total cart amount for the order
    };

    // Log the order to ensure it's correct
    console.log('Order Submitted:', confirmOrder);

    // Send the confirmOrder and cart items to the backend
    try {
      const response = await fetch('/api/confirm-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token'),  // Pass the auth token for authentication
        },
        body: JSON.stringify(confirmOrder),
      });

      const data = await response.json();
      if (data.success) {
        console.log('Order confirmed:', data);
        navigate('/thankyou');  // Redirect to a thank you page or another page as needed
      } else {
        console.error('Error confirming order:', data.message);
      }
    } catch (error) {
      console.error('Error submitting order:', error);
    }
  };

  return (
    <div className="confirm-order">
      <div className="confirm-order-left">
        <h2>Shipping Address</h2>
        <form className="shipping-form">
          <div>
            <label htmlFor="fullName">Full Name</label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              value={shippingDetails.fullName}
              onChange={handleShippingChange}
              required
            />
          </div>
          <div>
            <label htmlFor="district">District</label>
            <input
              type="text"
              id="district"
              name="district"
              value={shippingDetails.district}
              onChange={handleShippingChange}
              required
            />
          </div>
          <div>
            <label htmlFor="address">Address</label>
            <input
              type="text"
              id="address"
              name="address"
              value={shippingDetails.address}
              onChange={handleShippingChange}
              required
            />
          </div>
        </form>

        <h3>Payment Method</h3>
        <div className="payment-method">
          <div>
            <input
              type="radio"
              id="cash"
              name="paymentMethod"
              value="cash"
              checked={paymentMethod === 'cash'}
              onChange={handlePaymentChange}
            />
            <label htmlFor="cash">Cash on Delivery</label>
          </div>
          <div>
            <input
              type="radio"
              id="card"
              name="paymentMethod"
              value="card"
              checked={paymentMethod === 'card'}
              onChange={handlePaymentChange}
            />
            <label htmlFor="card">Credit Card</label>
          </div>
        </div>

        <button onClick={handleSubmitOrder} className="submit-order-btn">
          Confirm Order
        </button>
      </div>

      <div className="confirm-order-right">
        <h2>Cart Items</h2>
        <div className="cart-items-list">
          {all_product.map((product) => {
            if (cartItems[product.id] > 0) {
              return (
                <div key={product.id} className="cart-item">
                  <img src={product.image} alt={product.name} className="cart-item-image" />
                  <div className="cart-item-info">
                    <p>{product.name}</p>
                    <p>Quantity: {cartItems[product.id]}</p>
                    <p>Price: TK {product.new_price * cartItems[product.id]}</p>
                  </div>
                </div>
              );
            }
            return null;
          })}
        </div>
        <div className="cart-total">
          <h3>Total: TK {getTotalCartAmount()}</h3>
        </div>
      </div>
    </div>
  );
};

export default ConfirmOrder;
