import React, { useState } from "react";
import axios from "axios";
import "./VirtualTry.css";

const TryVirtually = () => {
  const [clothImage, setClothImage] = useState(null);
  const [personImage, setPersonImage] = useState(null);
  const [renderedImage, setRenderedImage] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleClothImageChange = (event) => {
    setClothImage(event.target.files[0]);
  };

  const handlePersonImageChange = (event) => {
    setPersonImage(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!clothImage || !personImage) {
      alert("Please upload both a cloth and person image.");
      return;
    }

    const formData = new FormData();
    formData.append("cloth", clothImage);
    formData.append("image", personImage);

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:4000/upload-viton", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.data.success) {
        setRenderedImage(`http://localhost:4000/images/result.jpg`);
      } else {
        alert("Image processing failed. Please try again.");
      }
    } catch (error) {
      console.error("Upload error:", error);
      alert("Server error occurred during image processing.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On</h1>
        <p className="virtual-try-description">
          Upload a clothing image and a person image to see how the clothes look on the person.
        </p>

        <div className="button-section">
          <input
            type="file"
            accept="image/*"
            onChange={handleClothImageChange}
            className="upload-image-button"
          />
          <input
            type="file"
            accept="image/*"
            onChange={handlePersonImageChange}
            className="upload-image-button"
          />
          <button onClick={handleUpload} className="button open-camera-button">
            Try On
          </button>
        </div>

        <div className="image-comparison-container">
          <div className="image-preview">
            <h2 className="preview-title">Person Image</h2>
            {personImage && <img src={URL.createObjectURL(personImage)} alt="Person" className="preview-image" />}
          </div>
          <div className="image-preview">
            <h2 className="preview-title">Cloth Image</h2>
            {clothImage && <img src={URL.createObjectURL(clothImage)} alt="Cloth" className="preview-image" />}
          </div>
          <div className="image-rendered">
            <h2 className="rendered-title">Rendered Image</h2>
            {loading ? (
              <p>Loading...</p>
            ) : renderedImage ? (
              <img src={renderedImage} alt="Rendered" className="rendered-image" />
            ) : (
              <p>No image rendered yet</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TryVirtually;
