import React, { useContext, useState, useEffect } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import './Checkout.css';

const Checkout = () => {
    const { cartItems, all_product, getTotalCartAmount } = useContext(ShopContext);
    const [orderPlaced, setOrderPlaced] = useState(false);
    const [shippingDetails, setShippingDetails] = useState({
      name: '',
      phone: '',
      city: '',
      address: '',
    });
    const [paymentMethod, setPaymentMethod] = useState('');
    const [cities, setCities] = useState([]);
    const [filteredCities, setFilteredCities] = useState([]);
    const [searchCity, setSearchCity] = useState(''); // Used to filter cities

    // List of cities in Bangladesh (unsorted initially)
    useEffect(() => {
        const bangladeshCities = [
            "Dhaka", "Chittagong", "Khulna", "Rajshahi", "Sylhet", "Barisal", "Rangpur", "Mymensingh",
            "Cumilla", "Jessore", "Narsingdi", "Narayanganj", "Tangail", "Brahmanbaria", "Bogra", "Pabna",
            "Chapainawabganj", "Madaripur", "Shariatpur", "Lakshmipur", "Moulvibazar", "Habiganj", "Kurigram",
            "Kohalpur", "Chandpur", "Manikganj", "Naogaon", "Feni", "Satkhira", "Khagrachari", "Chandraghona",
            "Patuakhali", "Meherpur", "Jhenaidah", "Rajbari", "Netrakona", "Sirajganj", "Sherpur", "Magura",
            "Rangamati", "Nilgiri", "Dinajpur", "Moulvi Bazar", "Jhalokathi", "Pirojpur", "Barisal",
            "Sylhet", "Mymensingh", "Rajshahi", "Khulna", "Chittagong", "Dhaka"
        ];

        // Sorting cities in ascending order
        const sortedCities = bangladeshCities.sort();
        setCities(sortedCities);
        setFilteredCities(sortedCities); // Initially show all cities
    }, []);

    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setShippingDetails({ ...shippingDetails, [name]: value });

      if (name === 'city') {
        setSearchCity(value);
        // Filter cities based on the input text (case-insensitive)
        const filtered = cities.filter(city => city.toLowerCase().includes(value.toLowerCase()));
        setFilteredCities(filtered);
      }
    };

    const handleOrderPlacement = async () => {
      if (shippingDetails.name && shippingDetails.phone && shippingDetails.city && shippingDetails.address && paymentMethod) {
        const orderData = {
          shippingDetails,
          cartData: cartItems,  // Send current cart data to backend
        };

        try {
          const response = await fetch('http://localhost:4000/placeorder', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'auth-token': localStorage.getItem('auth-token'),  // Assuming you're using JWT for auth
            },
            body: JSON.stringify(orderData),
          });

          const data = await response.json();

          if (data.success) {
            setOrderPlaced(true);
            alert(data.message);  // Show success message
          } else {
            alert("Order placement failed. Please try again.");
          }
        } catch (error) {
          console.error("Error placing order:", error);
          alert("An error occurred. Please try again.");
        }
      } else {
        alert('Please fill in all details and select a payment method');
      }
    };

    return (
      <div className="checkout-page">
        <div className="checkout-left">
          <h2>Shipping Address</h2>
          <input type="text" name="name" placeholder="Name" value={shippingDetails.name} onChange={handleInputChange} />
          <input type="text" name="phone" placeholder="Phone" value={shippingDetails.phone} onChange={handleInputChange} />

          <div className="city-container">
            <input 
              type="text" 
              name="city" 
              placeholder="Search for City"
              value={shippingDetails.city} 
              onChange={handleInputChange}
              className="city-input"
            />
            {filteredCities.length > 0 && (
              <div className="city-dropdown">
                {filteredCities.map((city, index) => (
                  <div 
                    key={index} 
                    className="city-option" 
                    onClick={() => setShippingDetails({ ...shippingDetails, city })}
                  >
                    {city}
                  </div>
                ))}
              </div>
            )}
          </div>

          <input type="text" name="address" placeholder="Address" value={shippingDetails.address} onChange={handleInputChange} />

          <h3>Payment Method</h3>
          <div className="checkout-payment-options">
            <label>
              <input type="radio" name="payment" value="card" onChange={() => setPaymentMethod('Card')} /> Card Payment
            </label>
            <label>
              <input type="radio" name="payment" value="cash" onChange={() => setPaymentMethod('Cash')} /> Cash Payment
            </label>
          </div>
          <button onClick={handleOrderPlacement}>Place Order</button>
          {orderPlaced && <p>Order placed</p>}
        </div>

        <div className="checkout-right">
          <h2>Your Selected Products</h2>
          {all_product.map((product) =>
            cartItems[product.id] > 0 ? (
              <div key={product.id} className="checkout-product">
                <img src={product.image} alt={product.name} className="checkout-product-image" />
                <p>{product.name}</p>
                <p>Quantity: {cartItems[product.id]}</p>
                <p>Price: TK {product.new_price * cartItems[product.id]}</p>
              </div>
            ) : null
          )}
          <h3>Total: TK {getTotalCartAmount()}</h3>
        </div>
      </div>
    );
};

export default Checkout;
