import React, { useState, useCallback, useRef } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';

const TryVirtually = () => {
  const [image, setImage] = useState(null);
  const [virtualTryOnImage, setVirtualTryOnImage] = useState(null); // New dress output
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const webcamRef = useRef(null);

  // Capture image from webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    generateVirtualTryOn(imageSrc);  // Generate virtual try-on output automatically after capture
    setIsCameraOpen(false);
  }, [webcamRef]);

  const handleImageUpload = (event) => {
    const uploadedImage = URL.createObjectURL(event.target.files[0]);
    setImage(uploadedImage);
    generateVirtualTryOn(uploadedImage);  // Generate virtual try-on output automatically after upload
  };

  // Simulate virtual try-on by modifying the image or returning a placeholder image
  const generateVirtualTryOn = (uploadedImage) => {
    // For now, we will use a placeholder image
    // You can replace this with actual backend logic in the future
    setVirtualTryOnImage('https://via.placeholder.com/300x400.png?text=New+Outfit'); 
  };

  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  const closeModal = () => {
    setIsCameraOpen(false);
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        <div className="virtual-try-options">
          {/* Button to open the camera */}
          <button onClick={handleOpenCamera} className="btn try-button">
            Open Camera
          </button>

          {/* File upload input for image upload */}
          <input type="file" accept="image/*" onChange={handleImageUpload} className="file-input" />

          {/* Preview section: Comparison between uploaded and virtual try-on */}
          {image && virtualTryOnImage && (
            <div className="comparison-section">
              <div className="comparison-box">
                <h3 className="preview-title">Uploaded Image</h3>
                <img src={image} alt="Uploaded" className="comparison-image" />
              </div>
              <div className="comparison-box">
                <h3 className="preview-title">Virtual Try-On</h3>
                <img src={virtualTryOnImage} alt="Virtual Try-On Output" className="comparison-image" />
              </div>
            </div>
          )}

          {/* Modal for the camera */}
          {isCameraOpen && (
            <div className="camera-modal">
              <div className="camera-overlay" onClick={closeModal}></div>
              <div className="camera-content">
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="webcam"
                />
                <div className="camera-actions">
                  <button onClick={captureImage} className="btn capture-button">Capture</button>
                  <button onClick={closeModal} className="btn close-button">Close</button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TryVirtually;
