import React, { useState } from 'react';
import './DescriptionBox.css';

const DescriptionBox = () => {
  const [showFullDescription, setShowFullDescription] = useState(false);
  const [showFullReview, setShowFullReview] = useState(false);

  const handleDescriptionToggle = () => {
    setShowFullDescription(prev => !prev);
  };

  const handleReviewToggle = () => {
    setShowFullReview(prev => !prev);
  };

  return (
    <div className="descriptionbox">
      <div className="descriptionbox-navigator">
        <div className="descriptionbox-nav-box">Description</div>
        <div 
          className="descriptionbox-nav-box fade" 
          onClick={handleReviewToggle}
        >
          Reviews (122)
        </div>
      </div>
      
      {/* Description Section */}
      <div className="descriptionbox-description">
        <h3>Description</h3>
        <p>
          This is an amazing product that helps you with various tasks. It's designed with cutting-edge technology to offer you the best experience. 
          {showFullDescription ? (
            <>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Cras sollicitudin massa at felis facilisis, at convallis arcu pharetra.
              Sed cursus ligula at nisi vehicula, in tincidunt metus congue. Vivamus bibendum leo ac lorem viverra, non varius odio mollis. 
              Curabitur et ligula risus. Integer cursus nec urna vel fermentum. Fusce tempus lacus ac ante pretium, a condimentum leo dictum. 
            </>
          ) : (
            <span>...<span className="read-more" onClick={handleDescriptionToggle}>Read more</span></span>
          )}
        </p>
      </div>

      {/* Reviews Section */}
      <div className="descriptionbox-reviews">
        <h3>Reviews</h3>
        <p>
          Customers are loving this product! Here's a brief snippet of the most recent review:
          {showFullReview ? (
            <>
              "This product has completely changed the way I work! It's so easy to use, and the results are incredible. Highly recommend it to anyone!"
            </>
          ) : (
            <span>...<span className="read-more" onClick={handleReviewToggle}>Read more</span></span>
          )}
        </p>
      </div>
    </div>
  );
}

export default DescriptionBox;
