import React, { useState, useEffect } from 'react';
import './CSS/UpdateProfile.css'; // Create a new CSS file for styling
import { useNavigate } from 'react-router-dom';

const UpdateProfile = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  useEffect(() => {
    // Fetch the current user's profile data when the component loads
    const fetchUserProfile = async () => {
      const token = localStorage.getItem('auth-token');
      if (!token) {
        navigate('/login'); // Redirect to login if no token exists
        return;
      }

      const response = await fetch('http://localhost:4000/getprofile', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': token, // Send token to authenticate user
        },
      });

      const data = await response.json();
      if (data.success) {
        setFormData({
          username: data.username,
          email: data.email,
          password: '', // Don't display the password
        });
      } else {
        alert('Error fetching profile');
      }
    };

    fetchUserProfile();
  }, [navigate]); // Use navigate in the dependency array

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('auth-token');
    if (!token) {
      alert('You must be logged in to update your profile');
      return;
    }

    const response = await fetch('http://localhost:4000/updateprofile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'auth-token': token, // Send token to authenticate user
      },
      body: JSON.stringify(formData),
    });

    const data = await response.json();

    if (data.success) {
      alert('Profile updated successfully');
      navigate('/'); // Redirect to home or profile page
    } else {
      alert(data.errors);
    }
  };

  return (
    <div className="update-profile">
      <h1>Update Profile</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          value={formData.username}
          onChange={handleChange}
          placeholder="Username"
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="New Password"
        />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default UpdateProfile;
