import React, { useState, useContext, useEffect } from 'react';
import { ShopContext } from './'; // Import your ShopContext
import axios from 'axios';

const OrderConfirmation = () => {
    const { cart, fetchCartData, token } = useContext(ShopContext); // Use your ShopContext for cart data and token
    const [address, setAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('Credit Card'); // Default payment method
    const [totalAmount, setTotalAmount] = useState(0);

    // Fetch the cart data on component mount
    useEffect(() => {
        fetchCartData();
    }, []);

    // Calculate total amount
    useEffect(() => {
        let total = 0;
        Object.keys(cart).forEach(itemId => {
            const item = cart[itemId];
            if (item && item.quantity > 0) {
                total += item.price * item.quantity;
            }
        });
        setTotalAmount(total);
    }, [cart]);

    // Handle order confirmation
    const confirmOrder = async () => {
        try {
            const response = await axios.post(
                'http://localhost:4000/confirmorder',
                { address, paymentMethod },
                {
                    headers: {
                        'auth-token': token,
                    },
                }
            );
            if (response.data.success) {
                alert('Order confirmed successfully!');
                // Optionally, clear cart or navigate to another page
            } else {
                alert('Failed to confirm order');
            }
        } catch (error) {
            console.error('Error confirming order:', error);
            alert('An error occurred while confirming the order');
        }
    };

    return (
        <div className="order-confirmation">
            <h2>Order Confirmation</h2>
            <div className="cart-items">
                <h3>Cart Items</h3>
                {Object.keys(cart).map(itemId => {
                    const item = cart[itemId];
                    if (item && item.quantity > 0) {
                        return (
                            <div key={itemId} className="cart-item">
                                <img src={item.image} alt={item.name} />
                                <div className="item-details">
                                    <p>{item.name}</p>
                                    <p>Price: ${item.price}</p>
                                    <p>Quantity: {item.quantity}</p>
                                </div>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>

            <div className="total-amount">
                <h3>Total Amount: ${totalAmount.toFixed(2)}</h3>
            </div>

            <div className="address-input">
                <h3>Address</h3>
                <textarea
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Enter your address"
                    rows="4"
                />
            </div>

            <div className="payment-method">
                <h3>Payment Method</h3>
                <select
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                >
                    <option value="Credit Card">Credit Card</option>
                    <option value="PayPal">PayPal</option>
                    <option value="Cash on Delivery">Cash on Delivery</option>
                </select>
            </div>

            <button onClick={confirmOrder} className="confirm-order-button">
                Confirm Order
            </button>
        </div>
    );
};

export default OrderConfirmation;
