import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const VirtualTry = () => {
  const { productId } = useParams();
  const [image, setImage] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [renderedImage, setRenderedImage] = useState(null);
  const webcamRef = useRef(null);
  const { addToCart, all_product } = useContext(ShopContext);

  const product = all_product.find((p) => p.id === Number(productId));

  // Capture Image from Webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    setIsCameraOpen(false);
  }, [webcamRef]);

  // Handle Image Upload from Local Device
  const handleImageUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  // Open Camera
  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  // Close Camera Modal
  const closeModal = () => {
    setIsCameraOpen(false);
  };

  // Add to Cart
  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id);
      alert('Product added to cart!');
    }
  };

  // Send image to server for processing and virtual try-on
  const handleTryOn = async () => {
    if (!image) {
      alert("Please upload an image or capture one using the camera.");
      return;
    }

    try {
      // Prepare the form data for sending to the server
      const formData = new FormData();
      formData.append('image', image);
      formData.append('productId', product.id);

      // Send POST request to server for processing the image
      const response = await axios.post('http://localhost:4000/virtual-try', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Handle the server response (assumed the server returns a processed image URL)
      if (response.data.success) {
        setRenderedImage(response.data.rendered_image_url);
      } else {
        alert('Error with virtual try-on process.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong during the try-on process.');
    }
  };

  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">Upload an image or use your camera to try on products virtually!</p>

        {product && (
          <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} className="preview-image" />
          </div>
        )}

        <div className="image-comparison-container">
          {image && (
            <div className="image-preview">
              <h3 className="preview-title">Your Uploaded Image</h3>
              <img src={image} alt="Preview" className="preview-image" />
            </div>
          )}
          {renderedImage && (
            <div className="image-rendered">
              <h3 className="rendered-title">Virtual Try Image</h3>
              <img src={renderedImage} alt="Rendered Try-On" className="rendered-image" />
            </div>
          )}
        </div>

        <div className="button-section">
          <button onClick={handleOpenCamera} className="button open-camera-button">Open Camera</button>
          <label htmlFor="file-upload" className="button upload-image-button">Upload Image</label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="file-input"
            style={{ display: 'none' }}
          />
        </div>

        {isCameraOpen && (
          <div className="camera-modal">
            <div className="camera-overlay" onClick={closeModal}></div>
            <div className="camera-content">
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                className="webcam"
              />
              <div className="camera-actions">
                <button onClick={captureImage} className="btn capture-button">Capture</button>
                <button onClick={closeModal} className="btn close-button">Close</button>
              </div>
            </div>
          </div>
        )}

        <button onClick={handleAddToCart} className="button add-to-cart-button">Add to Cart</button>
        <button onClick={handleTryOn} className="button try-on-button">Try On</button>
      </div>
    </div>
  );
};

export default TryV;
