import React, { useState } from "react";
import './VirtualTry.css';

const TryVirtually = () => {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [virtualTryOnImage, setVirtualTryOnImage] = useState(null); // Will hold the output of virtual try

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedImage(URL.createObjectURL(file));

      // Simulate a virtual try-on by setting a different image for demo
      // This can be replaced with actual virtual try-on output
      setVirtualTryOnImage('https://via.placeholder.com/300.png/09f/fff'); 
    }
  };

  const handleOpenCamera = () => {
    alert('Camera functionality not implemented yet.');
  };

  return (
    <div className="virtual-try-page">
      <h1 className="virtual-try-title">Try On Your Outfit Virtually</h1>
      <div className="try-on-container">
        <div className="uploaded-section">
          <h2>Uploaded Image</h2>
          {uploadedImage ? (
            <img src={uploadedImage} alt="Uploaded" className="try-on-image" />
          ) : (
            <div className="placeholder-box">Upload an Image</div>
          )}
        </div>
        <div className="virtual-section">
          <h2>Virtual Try-On Output</h2>
          {virtualTryOnImage ? (
            <img src={virtualTryOnImage} alt="Virtual Try-On Output" className="try-on-image" />
          ) : (
            <div className="placeholder-box">Virtual Output will appear here</div>
          )}
        </div>
      </div>

      <div className="buttons-container">
        <input type="file" accept="image/*" onChange={handleImageUpload} id="upload-button" />
        <button onClick={handleOpenCamera} className="camera-button">Open Camera</button>
      </div>
    </div>
  );
};

export default TryVirtually;
